import React from "react";

const Accordion = ({items}) => {
    const renderedItems = items.map( item => {
        return (
            <div className="title active">
                <i className="dropdown icon"></i>
                <h1>{item.title}</h1>
                
            </div>
        )
    })
    
}

export default Accordion;
